	(85, 6, '2014-03-12 14:43:38', '2014-03-12 14:43:38', '[spb_slider revslider_shortcode="homepage-slider" width="1/1" el_position="first last"] [spb_parallax parallax_type="image" bg_image="661" bg_type="cover" parallax_video_height="video-height" parallax_video_overlay="none" parallax_image_height="content-height" parallax_image_movement="fixed" parallax_image_speed="0.5" alt_background="none" el_class="area2" width="1/1" el_position="first last"]\r\n<p style="text-align: center;">[sf_button colour="gold" type="standard" size="standard" link="/profile/details" dropshadow="no"]<span style="color: #fff;">GET STARTED</span>[/sf_button]</p>\r\n\r\n<h1><span style="color: #ffffff;">Spend minutes filling out your style profile and never bother shopping again</span></h1>\r\n<img class="size-full aligncenter" alt="line" src="/wp-content/themes/dante-child/images/line.png" />\r\n<h3><span style="color: #ffffff;">Our technology is built to understand your size, style and preferences. Your personal stylist then takes over and builds you the perfect Enclothed box - That’s as much shopping as you’ll need to do.</span></h3>\r\n[/spb_parallax]', 'Home', '', 'publish', 'closed', 'closed', '', 'home', '', '', '2014-04-07 11:22:43', '2014-04-07 11:22:43', '', 0, 'http://enclothed.dev/?page_id=85', -1, 'page', '', 0),
