	(24, 1, '2014-03-11 17:21:00', '2014-03-11 17:21:00', '[fullwidth_text alt_background="none" el_class="area1" width="1/1" el_position="first last"]\r\n<h1 style="text-align: center;">Contact us</h1>\r\n<p class="georgia_text" style="text-align: center;">Stay in touch, we would love to hear from you!</p>\r\n<p style="text-align: center;">E-mail us at <a href="mailto:hello@enclothed.co.uk">hello@enclothed.co.uk</a>, comment on our <a href="/blog">blog</a> or find us on:</p>\r\n\r\n<div style="text-align: center;"><a href="https://twitter.com/Enclothed" target="_blank">[icon image="fa-twitter" size="medium" cont="yes" float="none"]</a><a href="https://www.facebook.com/Enclothed" target="_blank">[icon image="fa-facebook" size="medium" cont="yes" float="none"]</a><a href="http://instagram.com/enclothed" target="_blank">[icon image="fa-instagram" size="medium" cont="yes" float="none"]</a><a href="http://enclothedblog.tumblr.com/" target="_blank">[icon image="fa-tumblr" size="medium" cont="yes" float="none"]</a><a href="skype:Enclothed?call" target="_blank">[icon image="fa-skype" size="medium" cont="yes" float="none"]</a></div>\r\n[contact-form-7 id="994" title="Contact Us"]\r\n\r\n[/fullwidth_text]', 'Contact Us', '', 'publish', 'open', 'open', '', 'contact-us', '', '', '2014-04-10 15:19:27', '2014-04-10 15:19:27', '', 0, 'http://enclothed.dev/?page_id=24', 0, 'page', '', 0),
